                <Ionicons name="heart" size={16} color={selectedCategory === 'favorite_verses' ? '#FFFFFF' : (isDark ? '#D1D5DB' : '#4B5563')} />
                <Text style={[styles.categorySelectorText, selectedCategory === 'favorite_verses' && styles.categorySelectorTextActive]}>
