import { useState } from "react";
import { User, Bell, Moon, MessageSquare, Store, Share2, Heart, Edit, Instagram, Youtube, Clock, TestTube2, Camera } from "lucide-react";
import { SiTiktok } from "react-icons/si";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { MobileContainer } from "@/components/ui/mobile-container";
import { useApp } from "@/context/app-context";
import { useNotifications } from "@/context/notification-context";
import { useTheme, THEMES, Theme } from "@/hooks/use-theme";
import { Link } from "wouter";

export default function Settings() {
  const { user, setUser, settings, setSettings } = useApp();
  const { requestPermission, sendTestNotification, scheduleNotifications, getPermissionStatus } = useNotifications();
  const { theme, changeTheme } = useTheme();
  const [feedback, setFeedback] = useState("");
  const [isEditingProfile, setIsEditingProfile] = useState(false);
  const [editName, setEditName] = useState(user?.name || "");
  const [isAddingPhoto, setIsAddingPhoto] = useState(false);

  const handleSaveFeedback = () => {
    if (feedback.trim()) {
      // In a real app, this would send feedback via email API
      alert("Feedback enviado! Obrigado por compartilhar suas sugest√µes conosco.");
      setFeedback("");
    }
  };

  const handleSaveProfile = () => {
    if (user && editName.trim()) {
      setUser({
        ...user,
        name: editName.trim(),
      });
      setIsEditingProfile(false);
    }
  };

  const handleAddPhoto = (photoUrl?: string) => {
    if (user) {
      // For now, we'll use a placeholder avatar or remove photo
      const newPhotoUrl = photoUrl || `https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=3b82f6&color=fff&size=128`;
      setUser({
        ...user,
        photo: newPhotoUrl,
      });
    }
    setIsAddingPhoto(false);
  };

  const handleNotificationToggle = async (checked: boolean) => {
    if (checked && getPermissionStatus() !== 'granted') {
      const granted = await requestPermission();
      if (!granted) {
        alert("Precisamos da sua permiss√£o para enviar notifica√ß√µes carinhosas! Por favor, permita nas configura√ß√µes do navegador.");
        return;
      }
    }

    setSettings({
      ...settings,
      notificationsEnabled: checked,
    });

    scheduleNotifications(checked);
  };

  const handleTestNotification = () => {
    if (getPermissionStatus() === 'granted') {
      sendTestNotification();
    } else {
      alert("Por favor, ative as notifica√ß√µes primeiro.");
    }
  };

  const getMembershipDuration = () => {
    if (!user) return "Novo membro";
    const created = new Date(user.createdAt);
    const now = new Date();
    const diffTime = Math.abs(now.getTime() - created.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 1) return "Membro desde hoje";
    if (diffDays < 30) return `Membro h√° ${diffDays} dias`;
    const months = Math.floor(diffDays / 30);
    return `Membro h√° ${months} ${months === 1 ? 'm√™s' : 'meses'}`;
  };

  return (
    <MobileContainer>
      <div className="p-4 pb-24">

        {/* User Profile */}
        <Card className="mb-6">
          <CardContent className="p-6">
            <div className="flex items-center space-x-4">
              <div className="relative">
                <div className="w-16 h-16 rounded-full bg-primary flex items-center justify-center">
                  {user?.photo ? (
                    <img src={user.photo} alt="Profile" className="w-16 h-16 rounded-full object-cover" />
                  ) : (
                    <User className="w-6 h-6 text-primary-foreground" />
                  )}
                </div>
                <button
                  onClick={() => setIsAddingPhoto(true)}
                  className="absolute -bottom-1 -right-1 w-6 h-6 bg-secondary rounded-full flex items-center justify-center border-2 border-background hover:bg-secondary/80"
                  data-testid="button-add-photo"
                >
                  <Camera className="w-3 h-3 text-secondary-foreground" />
                </button>
              </div>
              <div className="flex-1">
                <h3 className="text-xl font-semibold mb-1" data-testid="text-user-name">
                  {user?.name || "Usu√°rio"}
                </h3>
                <p className="text-muted-foreground text-sm mb-2">
                  {getMembershipDuration()}
                </p>
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={() => {
                    setEditName(user?.name || "");
                    setIsEditingProfile(true);
                  }}
                  className="p-0 h-auto text-primary"
                  data-testid="button-edit-profile"
                >
                  <Edit className="w-3 h-3 mr-1" />
                  Editar nome
                </Button>
              </div>
            </div>

            {/* Photo Dialog */}
            <Dialog open={isAddingPhoto} onOpenChange={setIsAddingPhoto}>
              <DialogContent className="max-w-[95vw] w-full mx-auto">
                <DialogHeader>
                  <DialogTitle>Adicionar Foto</DialogTitle>
                </DialogHeader>
                <div className="text-center py-6">
                  <div className="w-24 h-24 mx-auto mb-4 rounded-full bg-muted flex items-center justify-center border-2 border-dashed border-border">
                    <Camera className="w-8 h-8 text-muted-foreground" />
                  </div>
                  <p className="text-sm text-muted-foreground mb-4">
                    Escolha uma op√ß√£o para sua foto de perfil:
                  </p>
                  <div className="space-y-2">
                    <input 
                      type="file" 
                      accept="image/*" 
                      className="hidden" 
                      id="photo-upload"
                      onChange={(e) => {
                        const file = e.target.files?.[0];
                        if (file && user) {
                          const reader = new FileReader();
                          reader.onload = (event) => {
                            const photoUrl = event.target?.result as string;
                            setUser({ ...user, photo: photoUrl });
                            setIsAddingPhoto(false);
                          };
                          reader.readAsDataURL(file);
                        }
                      }}
                    />
                    <Button 
                      onClick={() => document.getElementById('photo-upload')?.click()}
                      className="w-full"
                      data-testid="button-upload-photo"
                    >
                      Fazer Upload de Foto
                    </Button>
                    <Button 
                      variant="outline"
                      onClick={() => {
                        if (user) {
                          setUser({ ...user, photo: undefined });
                        }
                        setIsAddingPhoto(false);
                      }}
                      className="w-full"
                      data-testid="button-remove-photo"
                    >
                      Remover Foto
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>

            {/* Edit Name Dialog */}
            <Dialog open={isEditingProfile} onOpenChange={setIsEditingProfile}>
              <DialogContent className="max-w-[95vw] w-full mx-auto">
                <DialogHeader>
                  <DialogTitle>Editar Nome</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="edit-name">Nome</Label>
                    <Input
                      id="edit-name"
                      value={editName}
                      onChange={(e) => setEditName(e.target.value)}
                      placeholder="Seu nome"
                      data-testid="input-edit-name"
                    />
                  </div>
                  <div className="flex space-x-2 pt-4">
                    <Button onClick={handleSaveProfile} className="flex-1" data-testid="button-save-profile">
                      Salvar
                    </Button>
                    <Button 
                      variant="outline" 
                      onClick={() => setIsEditingProfile(false)}
                      data-testid="button-cancel-profile"
                    >
                      Cancelar
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </CardContent>
        </Card>

        {/* Settings Options */}
        <div className="space-y-4">
          {/* Theme Mode */}
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between mb-3">
                <div className="flex items-center">
                  <Moon className="w-5 h-5 text-primary mr-3" />
                  <h4 className="font-semibold">Modo do Tema</h4>
                </div>
                <Switch 
                  checked={theme !== 'light'}
                  onCheckedChange={(checked) => {
                    if (!checked) {
                      changeTheme('light');
                    } else {
                      // Quando ativa pela primeira vez, vai para dark como padr√£o
                      changeTheme('dark');
                    }
                  }}
                  data-testid="switch-theme-mode"
                />
              </div>
              {theme !== 'light' && (
                <div className="space-y-3 mt-4">
                  <p className="text-sm text-muted-foreground mb-3">Escolha a cor do aplicativo:</p>
                  <div className="grid grid-cols-2 gap-3">
                    {/* Op√ß√£o Branco/Padr√£o */}
                    <Button
                      variant="outline"
                      onClick={() => changeTheme('light')}
                      className="h-12 flex items-center justify-center"
                      data-testid="button-theme-light"
                    >
                      <div className="flex items-center space-x-2">
                        <div className="w-4 h-4 rounded-full bg-white border border-gray-300"></div>
                        <span className="text-sm">Branco</span>
                      </div>
                    </Button>

                    {/* Outras op√ß√µes de tema */}
                    {Object.entries(THEMES).filter(([key]) => key !== 'light').map(([themeKey, themeName]) => (
                      <Button
                        key={themeKey}
