                        onClick={() => changeTheme(themeKey as Theme)}
                        className="h-12 flex items-center justify-center"
                        data-testid={`button-theme-${themeKey}`}
                      >
                        <div className="flex items-center space-x-2">
                          <div className={`w-4 h-4 rounded-full ${
